import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaUserAlt, FaLock, FaEnvelope, FaPhone } from "react-icons/fa";

import './LoginSignUp.css';

const SignupForm = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setconfirmPassword] = useState("");
    const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    // Validation ก่อนส่งฟอร์ม
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phonePattern = /^0\d{9}$/;
    const passwordPattern = /^.{8,}$/; 

    if (!emailPattern.test(email)) {
        alert("Invalid email format!");
        return;
    }
    if (!phonePattern.test(phone)) {
        alert("Phone number must be 10 digits and start with 0!");
        return;
    }
    if (!passwordPattern.test(password)) {
        alert("Password must be at least 8 characters!");
        return;
    }
    if (password !== confirmPassword) {
        alert("Passwords do not match!");
        return;
    }

    const newUser = { firstName, lastName, username, email, phone, password};

    const response = await fetch("http://localhost:8080/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
    });

    if (response.ok) {
        alert("Registration successful!");
        navigate("/login");
    } else {
        const errorText = await response.text();
        alert(`Failed to register: ${errorText}`);
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-900 text-white">
        <div className="w-96 bg-gray-800 p-8 rounded-lg shadow-lg">
            <h1 className="text-2xl font-bold text-center mb-6">Sign Up</h1>
            <form onSubmit={handleSignup}>
                {[{ placeholder: "First Name", value: firstName, setter: setFirstName, icon: <FaUserAlt /> },
                  { placeholder: "Last Name", value: lastName, setter: setLastName, icon: <FaUserAlt /> },
                  { placeholder: "Username", value: username, setter: setUsername, icon: <FaUserAlt /> },
                  { placeholder: "Email", value: email, setter: setEmail, icon: <FaEnvelope />, type: "email" },
                  { placeholder: "Phone", value: phone, setter: setPhone, icon: <FaPhone />, type: "text" },
                  { placeholder: "Password", value: password, setter: setPassword, icon: <FaLock />, type: "password" },
                  { placeholder: "Confirm Password", value: confirmPassword, setter: setConfirmPassword, icon: <FaLock />, type: "password" }]
                  .map((input, index) => (
                    <div key={index} className="relative mb-4">
                        <input 
                            type={input.type || "text"} 
                            placeholder={input.placeholder} 
                            value={input.value} 
                            onChange={(e) => input.setter(e.target.value)}
                            required 
                            className="w-full bg-gray-700 text-white px-4 py-3 pl-10 rounded-lg border border-gray-600 focus:outline-none focus:ring-2 focus:ring-purple-500"
                        />
                        <div className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">{input.icon}</div>
                    </div>
                ))}
                <button type="submit" className="w-full bg-purple-600 hover:bg-purple-700 text-white font-bold py-3 rounded-lg transition">Sign Up</button>
            </form>
            <p className="text-center text-sm mt-4">Already have an account? <a href="/login" className="text-purple-400 hover:underline">Login</a></p>
        </div>
    </div>
);

};

export default SignupForm;
