import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaEye, FaEyeSlash  } from "react-icons/fa";

import axios from "axios";

// import './ProfilePage.css';

const ProfilePage = ({ authUser, setAuthUser }) => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);

  const [isEditing, setIsEditing] = useState(false);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword((prev) => !prev);
  }

  const userId = authUser?.id;

  useEffect(() => {
    if (!userId) {
      navigate("/login");
      return;
    }

    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/users/${userId}`);
        const data = response.data;
        setUserData(data);

        setFirstName(data.firstName);
        setLastName(data.lastName);
        setEmail(data.email);
        setUsername(data.username);
        setPhone(data.phone || "");
        setPassword(data.password || "");
      } catch (error) {
        console.error("Error fetching user data:", error);
        navigate("/login"); 
      }
    };
    fetchUserData();
  }, [userId, navigate]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setFirstName(userData.firstName);
    setLastName(userData.lastName);
    setEmail(userData.email);
    setUsername(userData.username);
    setPhone(userData.phone || "");
    setPassword(userData.password || "");
    setIsEditing(false);
  };

  const handleSave = async () => {
    try {
      const updatedData = { firstName, lastName, email, username, phone };

      if (password) {
        updatedData.password = password;
      }

      await axios.put(`http://localhost:8080/users/${userId}`, updatedData);

      setUserData(updatedData);
      setIsEditing(false);
      setPassword("");
    } catch (error) {
      console.error("Error updating user data:", error);
    }
  };

  const handleLogout = () => {
    setAuthUser(null);
    navigate("/login");
  };

  if (!userData) {
    return <p className="text-center text-gray-600">Loading...</p>;
  }

  return (
    <div className="container mx-auto p-6">
      <div className="max-w-md mx-auto bg-gray-800 text-white p-6 rounded-lg shadow-lg">
        <h1 className="text-2xl font-bold mb-4 text-center">User Profile</h1>
        <div className="space-y-4">
          {["First Name", "Last Name", "Email", "Username", "Phone"].map((label, index) => (
            <div key={index}>
              <label className="block text-sm font-medium">{label}:</label>
              <input
                type={label === "Email" ? "email" : "text"}
                value={eval(label.toLowerCase().replace(" ", ""))}
                onChange={(e) => eval(`set${label.replace(" ", "")} (e.target.value)`) }
                disabled={!isEditing}
                className="w-full p-2 bg-gray-700 border border-gray-500 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>
          ))}

          <div>
            <label className="block text-sm font-medium">Password:</label>
            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                value={showPassword ? password : "********"}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter new password (optional)"
                disabled={!isEditing}
                className="w-full p-2 pr-10 bg-gray-700 border border-gray-500 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
              <span className="absolute right-3 top-2 cursor-pointer text-white" onClick={togglePasswordVisibility}>
                {showPassword ? <FaEyeSlash /> : <FaEye />}
              </span>
            </div>
          </div>
        </div>

        <div className="mt-4 space-x-2 flex justify-center">
          {isEditing ? (
            <>
              <button className="px-4 py-2 bg-green-500 rounded-lg hover:bg-green-600" onClick={handleSave}>Save</button>
              <button className="px-4 py-2 bg-red-500 rounded-lg hover:bg-red-600" onClick={handleCancel}>Cancel</button>
            </>
          ) : (
            <button className="px-4 py-2 bg-gray-600 rounded-lg hover:bg-gray-700" onClick={handleEdit}>Edit</button>
          )}
        </div>

        <button className="mt-4 w-full bg-blue-500 py-2 rounded-lg hover:bg-blue-600" onClick={handleLogout}>Logout</button>
      </div>
    </div>
  );
};

export default ProfilePage;
