import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaEye, FaEyeSlash  } from "react-icons/fa";

import axios from "axios";


const ProfilePage = ({ authUser, setAuthUser }) => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);

  const [isEditing, setIsEditing] = useState(false);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword((prev) => !prev);
  }

  const userId = authUser?.id;

  useEffect(() => {
    if (!userId) {
      navigate("/login");
      return;
    }

    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/users/${userId}`);
        const data = response.data;
        setUserData(data);

        setFirstName(data.firstName);
        setLastName(data.lastName);
        setEmail(data.email);
        setUsername(data.username);
        setPhone(data.phone || "");
        setPassword(data.password || "");
      } catch (error) {
        console.error("Error fetching user data:", error);
        navigate("/login"); 
      }
    };

    fetchUserData();
  }, [userId, navigate]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setFirstName(userData.firstName);
    setLastName(userData.lastName);
    setEmail(userData.email);
    setUsername(userData.username);
    setPhone(userData.phone || "");
    setPassword(userData.password || "");

    setIsEditing(false);
  };

  const handleSave = async () => {
    try {
      const updatedData = { firstName, lastName, email, username, phone };

      if (password) {
        updatedData.password = password;
      }

      await axios.put(`http://localhost:8080/users/${userId}`, updatedData);

      setUserData(updatedData);
      setIsEditing(false);
      setPassword("");
    } catch (error) {
      console.error("Error updating user data:", error);
    }
  };

  const handleLogout = () => {
    setAuthUser(null);
    navigate("/login");
  };

  if (!userData) {
    return <p className="text-center text-gray-600">Loading...</p>;
  }

  return (
    <div className="min-h-screen flex items-center justify-center ">
      <div className="bg-purple-800 bg-opacity-90 p-8 rounded-2xl shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold text-center mb-6">User Profile</h2>
        <div className="space-y-4">
          <input type="text" value={firstName} onChange={(e) => setFirstName(e.target.value)} className="w-full p-2 rounded bg-gray-700 text-white" placeholder="First Name" disabled={!isEditing} />
          <input type="text" value={lastName} onChange={(e) => setLastName(e.target.value)} className="w-full p-2 rounded bg-gray-700 text-white" placeholder="Last Name" disabled={!isEditing} />
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} className="w-full p-2 rounded bg-gray-700 text-white" placeholder="Email" disabled={!isEditing} />
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} className="w-full p-2 rounded bg-gray-700 text-white" placeholder="Username" disabled={!isEditing} />
          <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} className="w-full p-2 rounded bg-gray-700 text-white" placeholder="Phone" disabled={!isEditing} />
          <div className="relative">
            <input type={showPassword ? "text" : "password"} value={password} onChange={(e) => setPassword(e.target.value)} className="w-full p-2 rounded bg-gray-700 text-white pr-10" placeholder="Password" disabled={!isEditing} />
            <span className="absolute right-3 top-3 cursor-pointer" onClick={() => setShowPassword(!showPassword)}>
              {showPassword ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>
        </div>
        <div className="flex justify-between mt-6">
          {isEditing ? (
            <>
              <button onClick={handleSave} className="bg-green-500 px-4 py-2 rounded-lg">Save</button>
              <button onClick={() => setIsEditing(false)} className="bg-gray-500 px-4 py-2 rounded-lg">Cancel</button>
            </>
          ) : (
            <button onClick={() => setIsEditing(true)} className="bg-gray-400 px-4 py-2 rounded-lg">Edit</button>
          )}
        </div>
        <button onClick={handleLogout} className="w-full bg-red-500 px-4 py-2 rounded-lg mt-4">Logout</button>
      </div>
    </div>
  );

  
};

export default ProfilePage;
