import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaEye, FaEyeSlash  } from "react-icons/fa";

import axios from "axios";

// import './ProfilePage.css';

const ProfilePage = ({ authUser, setAuthUser }) => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);

  const [isEditing, setIsEditing] = useState(false);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword((prev) => !prev);
  }

  const userId = authUser?.id;

  useEffect(() => {
    if (!userId) {
      navigate("/login");
      return;
    }

    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/users/${userId}`);
        const data = response.data;
        setUserData(data);

        setFirstName(data.firstName);
        setLastName(data.lastName);
        setEmail(data.email);
        setUsername(data.username);
        setPhone(data.phone || "");
        setPassword(data.password || "");
      } catch (error) {
        console.error("Error fetching user data:", error);
        navigate("/login"); 
      }
    };

    fetchUserData();
  }, [userId, navigate]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setFirstName(userData.firstName);
    setLastName(userData.lastName);
    setEmail(userData.email);
    setUsername(userData.username);
    setPhone(userData.phone || "");
    setPassword(userData.password || "");

    setIsEditing(false);
  };

  const handleSave = async () => {
    try {
      const updatedData = { firstName, lastName, email, username, phone };

      if (password) {
        updatedData.password = password;
      }

      await axios.put(`http://localhost:8080/users/${userId}`, updatedData);

      setUserData(updatedData);
      setIsEditing(false);
      setPassword("");
    } catch (error) {
      console.error("Error updating user data:", error);
    }
  };

  const handleLogout = () => {
    setAuthUser(null);
    navigate("/login");
  };

  if (!userData) {
    return <p className="text-center text-gray-600">Loading...</p>;
  }

  return (
    <div className="flex justify-center items-center min-h-[calc(100vh-60px)] mt-[60px]">
      <div className="w-[420px] bg-white/10 rounded-lg backdrop-blur-md shadow-xl p-10 text-white transition-transform transform hover:scale-105">
        <h1 className="text-3xl font-bold text-center mb-6">User Profile</h1>
        <div className="space-y-6">
          {[['First Name', firstName, setFirstName],
            ['Last Name', lastName, setLastName],
            ['Email', email, setEmail],
            ['Username', username, setUsername],
            ['Phone', phone, setPhone]].map(([label, value, setter], index) => (
            <div key={index}>
              <label className="block text-sm font-medium">{label}:</label>
              <input
                type="text"
                value={value}
                onChange={(e) => setter(e.target.value)}
                disabled={!isEditing}
                className="w-full p-3 border border-gray-400 rounded bg-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>
          ))}
          <div>
            <label className="block text-sm font-medium">Password:</label>
            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                value={showPassword ? password : "********"}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter new password (optional)"
                disabled={!isEditing}
                className="w-full p-3 pr-10 border border-gray-400 rounded bg-gray-700 text-white focus:outline-none"
              />
              <span className="absolute right-3 top-3 cursor-pointer" onClick={togglePasswordVisibility}>
                {showPassword ? <FaEyeSlash /> : <FaEye />}
              </span>
            </div>
          </div>
        </div>
        <div className="mt-6 flex justify-center space-x-4">
          {isEditing ? (
            <>
              <button className="px-4 py-2 rounded bg-green-500 hover:bg-green-600 transition" onClick={handleSave}>Save</button>
              <button className="px-4 py-2 rounded bg-red-500 hover:bg-red-600 transition" onClick={handleCancel}>Cancel</button>
            </>
          ) : (
            <button className="px-4 py-2 rounded bg-blue-500 hover:bg-blue-600 transition" onClick={handleEdit}>Edit</button>
          )}
        </div>
        <button className="w-full mt-4 px-4 py-2 rounded bg-gray-600 hover:bg-gray-700 transition" onClick={handleLogout}>Logout</button>
      </div>
    </div>
  );
};

export default ProfilePage;
