import React from "react";
import { Link } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";

const Navbar = ({ authUser }) => {
  return (
    <header className="bg-red-500">
      <Link to="/home" className="text-2xl font-bold text-white no-underline">
        Gemini
      </Link>

      <nav className="flex items-center gap-x-10">
        {["home", "observation", "astrodata", "sciplan"].map((item, index) => (
          <Link
            key={index}
            to={`/${item}`}
            className="relative text-lg text-white font-medium no-underline group"
          >
            {item.charAt(0).toUpperCase() + item.slice(1)}
            <span className="absolute left-0 bottom-[-6px] w-full h-[3px] bg-white rounded transition-transform scale-x-0 group-hover:scale-x-100 origin-right group-hover:origin-left"></span>
          </Link>
        ))}

        {authUser ? (
          <Link to="/profile" className="text-white">
            <FaUserCircle size={28} />
          </Link>
        ) : (
          <Link
            to="/login"
            className="px-6 py-3 text-lg font-bold text-white border-2 border-white rounded-lg transition duration-300 
            hover:bg-white hover:text-[#162938] hover:-translate-y-1 no-underline"
          >
            Login
          </Link>
        )}
      </nav>
    </header>
  );
};

export default Navbar;
