import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaUserAlt, FaLock, FaEnvelope, FaPhone } from "react-icons/fa";

const SignupForm = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setconfirmPassword] = useState("");
    const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    // Validation ก่อนส่งฟอร์ม
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phonePattern = /^0\d{9}$/;
    const passwordPattern = /^.{8,}$/; 

    if (!emailPattern.test(email)) {
        alert("Invalid email format!");
        return;
    }
    if (!phonePattern.test(phone)) {
        alert("Phone number must be 10 digits and start with 0!");
        return;
    }
    if (!passwordPattern.test(password)) {
        alert("Password must be at least 8 characters!");
        return;
    }
    if (password !== confirmPassword) {
        alert("Passwords do not match!");
        return;
    }

    const newUser = { firstName, lastName, username, email, phone, password};

    const response = await fetch("http://localhost:8080/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
    });

    if (response.ok) {
        alert("Registration successful!");
        navigate("/login");
    } else {
        const errorText = await response.text();
        alert(`Failed to register: ${errorText}`);
    }
  };

  return (
    <main className="pt-[100px] flex justify-center items-center min-h-screen bg-gradient-to-br from-purple-900 to-indigo-900 text-white">
        <div className="max-w-md w-full bg-white/10 rounded-lg backdrop-blur-lg shadow-2xl p-10 transition-transform transform hover:scale-105">
            <h1 className="text-3xl font-bold text-center mb-6">User Profile</h1>
            <div className="space-y-6">
                {[['First Name', firstName, setFirstName],
                  ['Last Name', lastName, setLastName],
                  ['Email', email, setEmail],
                  ['Username', username, setUsername],
                  ['Phone', phone, setPhone]].map(([label, value, setter], index) => (
                    <div key={index}>
                        <label className="block text-xl font-semibold text-white mb-2">{label}:</label>
                        <input
                            type="text"
                            value={value}
                            onChange={(e) => setter(e.target.value)}
                            disabled={!isEditing}
                            className="w-full p-3 border border-white/30 rounded-lg bg-white text-gray-900 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-400"
                        />
                    </div>
                ))}
                <div>
                    <label className="block text-xl font-semibold text-white mb-2">Password:</label>
                    <div className="relative">
                        <input
                            type={showPassword ? "text" : "password"}
                            value={showPassword ? password : "********"}
                            onChange={(e) => setPassword(e.target.value)}
                            disabled={!isEditing}
                            className="w-full p-3 pr-10 border border-white/30 rounded-lg bg-white text-gray-900 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-400"
                        />
                        <span className="absolute right-3 top-3 cursor-pointer text-gray-500" onClick={togglePasswordVisibility}>
                            {showPassword ? <FaEyeSlash /> : <FaEye />}
                        </span>
                    </div>
                </div>
            </div>
            <div className="mt-6 flex justify-center space-x-4">
                {isEditing ? (
                    <>
                        <button className="w-1/2 px-4 py-2 rounded-full transition text-white bg-green-500 hover:bg-green-600" onClick={() => setIsEditing(false)}>Save</button>
                        <button className="w-1/2 px-4 py-2 rounded-full transition text-white bg-red-500 hover:bg-red-600" onClick={handleCancel}>Cancel</button>
                    </>
                ) : (
                    <button className="w-2/3 px-4 py-2 rounded-full transition text-white bg-gray-600 hover:bg-gray-700" onClick={handleEdit}>Edit</button>
                )}
            </div>
            <button className="w-full mt-6 px-4 py-3 rounded-full bg-purple-600 hover:bg-purple-700 transition shadow-lg" onClick={handleLogout}>Logout</button>
        </div>
    </main>
);
};

export default SignupForm;
